import styled from "@emotion/styled";

export const LoaderWrapper = styled('div')`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1999;

    .loader_box {
        position: relative;


        
    }
`;
