import { useEffect, useState } from "react";
import { WishlistWrapper } from "../Styles/Wishlist-Style";
import axios from "axios";
import { Delete_Wishlist_URL, Fetch_Wishlist_URL, Product_Image_URL } from "../Api/Api";
import { use } from "react";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Bounce } from 'react-toastify';



function Wishlist() {

    const [wishlistItems, setWishlistItems] = useState([]);

    useEffect(() => {
        const login = localStorage.getItem("login");

        if (login == "true"){
            const user = localStorage.getItem("userData");
            const userData = JSON.parse(user);

            axios.get(`${Fetch_Wishlist_URL}?name=${userData.user}`)
            .then(res => {
                const products = res.data.wishlist.productlist;
                const jsonProducts = JSON.parse(products);
                setWishlistItems(jsonProducts);
                console.log(jsonProducts);
            })
            .catch(err => {
                console.log(err);
            })
        }
    }, []);

    const handleDeleteWishlist = (id) => {
        const input = {
            id: id
        }
        axios.post(Delete_Wishlist_URL, input, {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        })
        .then(res => {
            console.log(res);
            if(res.data.status == "200") {
                toast.warn('Blog Deleted', {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                    transition: Bounce,
                });
            }
        })
        .catch(err => {
            console.log(err);
        })
    }

    return(
        <>
            <WishlistWrapper>
                <div className="wrapper_inner">
                    <div className="page_head">
                        <h2>My Wishlist</h2>
                    </div>
                    <div className="wishlist_sec">
                        <div className="sec_inner">
                            {
                                wishlistItems.length > 0 ? 
                                wishlistItems.map((item, i) => {
                                    return(
                                        <div className="sec_box" key={i}>
                                            <div className="box_inner">
                                                <div className="img_box">
                                                    <img src={`${Product_Image_URL}/${item.pimage}`} alt="image" />
                                                </div>
                                                <div className="content_box">
                                                    <h5>{item.pname}</h5>
                                                    <p>Price : <span><i className="fa-solid fa-indian-rupee-sign"></i>{item.price} {item.pCategory === 'Electronics' ? '' : `(${item.unit})`}</span></p>
                                                    <div className="action_box">
                                                        <a><i className="fa-solid fa-cart-shopping"></i></a>
                                                        <a><i className="fa-solid fa-trash-can" onClick={() => handleDeleteWishlist(item.id)}></i></a>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    );
                                })
                                :
                                <div className="empty_msg">
                                    <p>Please add products to your wishlist.</p>
                                </div>
                            }
                           
                        </div>
                    </div>
                </div>
            </WishlistWrapper>
        </>
    );
}

export default Wishlist
