import styled from "@emotion/styled";

export const LoaderWrapper = styled('div')`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1999;

    .loader_box {
        position: relative;


        .container{
            display: flex;
            position: relative;
            height: 100px;
            width: 100px;

            .outer-circle{
                background-color: #580000;
                height: 100px;
                width: 100px;
                border-radius: 100%;
                }
                .dash{
                position: absolute;
                height: 100px;
                width: 100px;
                left: 0;
                top: 0;
                }
                .inner-circle{
                background-color: white;
                height: 80px;
                width: 80px;
                border-radius: 100%;
                top: 10px;
                left: 10px;
                position: absolute;
                }
                .arrow{
                height: 76px;
                width: 10px;
                background-color: #580000;
                position: absolute;
                left: calc(50% - 5px);
                top: 2px;
                clip-path: polygon(50% 0, 100% 50%, 50% 100%, 0 50%);
                animation: 3s linear 1s infinite turn;
                overflow: hidden;
                }

                .arrow-alternate{
                background-color: blue;
                height: 38px;
                width: 10px;
                }

                .pin{
                background-color: #CCC;
                height: 20px;
                width: 20px;
                position: absolute;
                border-radius: 100%;
                top: calc(50% - 10px);
                left: calc(50% - 10px);
                }
                @keyframes turn {
                from {
                    transform: rotate(0deg);
                }
                to {
                    transform: rotate(360deg);
                }
            }
        }
    }
`;
