import styled from "@emotion/styled";

export const LoaderWrapper = styled('div')`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1999;

    .loader_box {
        position: relative;


        .pl {
            display: block;
            margin: auto;
            width: 8em;
            height: auto;

            .pl__star,
            .pl__worm {
                animation-duration: 10s;
                animation-timing-function: linear;
                animation-iteration-count: infinite;
            }
            .pl__star {
                animation-name: spin;
                transform-origin: 32px 32px;
            }
            .pl__worm {
                animation-name: worm-length, worm-move;
            }

            /* Animations */
            @keyframes spin {
                from {
                    transform: rotate(0);
                }
                to {
                    transform: rotate(1turn);
                }
            }

            @keyframes worm-length {
	0%, 100% {
		stroke-dasharray: 19.08px, 171.8px;
	}
	25%, 75% {
		stroke-dasharray: 57.26px, 133.62px;
	}
	50% {
		stroke-dasharray: 95.44px, 95.44px;
	}
}

@keyframes worm-move {
	0%, 100% {
		stroke-dashoffset: 0px;
	}
	25% {
		stroke-dashoffset: -47.72px;
	}
	50% {
		stroke-dashoffset: -95.44px;
	}
	75% {
		stroke-dashoffset: -143.16px;
	}
        }
    }
`;
